# -*- coding=utf-8 -*-from flask import Flaskfrom flask_cors import *from settings.appConfig import DevConfigfrom dbs.db import DBfrom nav import navdef create_app():    app = Flask(__name__,instance_relative_config=True)    CORS(app, supports_credentials=True) #允许跨域    app.config.from_object(DevConfig)    #注册蓝图    app.register_blueprint(nav,url_prefix='/nav')    #treeData数据表nav_tree    db =  DB()    create_nav_tree = """create table if not exists nav_tree(\        id INTEGER PRIMARY KEY AUTOINCREMENT,\        user_name VARCHAR(64) NOT NULL,\        user_id VARCHAR(64) NOT NULL,\        tree_data TEXT\    )"""    #文件信息表files    create_files = """create table if not exists files(\        id VARCHAR(128) PRIMARY KEY NOT NULL,\        user_id VARCHAR(64)  NOT NULL,\        file_name VARCHAR(128) NOT NULL,\        is_leaf INTEGER NOT NULL,\        file_path VARCHAR(256) NOT NULL    )"""    db.doCreateTable(create_nav_tree)    db.doCreateTable(create_files)    return appapp = create_app()app.run()