import sqlite3from settings.appConfig import DevConfigfrom utils.dic_factory import dic_factoryclass DB(object):    def __init__(self):        self.conn = sqlite3.connect(DevConfig.DATABASE)        self.cursor = self.conn.cursor()        self.conn.row_factory = sqlite3.Row        #self.conn.row_factory = dic_factory.dic_result    #创建表格    def doCreateTable(self,sql):        try:            self.cursor.execute(sql)            return True        except Exception as e:            print("创建表出错",e)            return False    def doInsert(self,sql,arg):        try:            data = self.cursor.executemany(sql,arg)            self.conn.commit()            print("插入数据成功")            return True        except Exception as e:            print("插入数据出错",e)            return False    def doUpdate(self,sql,arg):        try:            data = self.cursor.execute(sql,arg)            self.conn.commit()            print("更新数据成功")            return True        except Exception as e:            print("更新数据失败",e)            return False    def doSelect(self,sql,arg):        try:            self.cursor.execute(sql,arg)            data = self.cursor.fetchall()            return data        except Exception as e:            return None    def doClose(self):        self.conn.close()